package com.loiane.cursoJava.aula11;

public class VariaveisPontoFlutuante {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double valorPassagem = 6.90;
		float valorTomate = 10.99f; // Sem o f ou F dá erro
		double d2 = 1.234e2; // O double permite usar notação científica
		
		System.out.println("Valor da passagem: " + valorPassagem);
		System.out.println("Valor do tomate: " + valorTomate);
		System.out.println("Double com notação científica: " + d2);
		
	}

}
